$.validator.setDefaults({
	errorElement: "span",
	errorPlacement: function(e, r) {
		e.appendTo(r.is(":radio") || r.is(":checkbox") ? r.parent().parent().parent() : r.parent())
	},
	errorClass: "help-block m-b-none",
	validClass: "help-block m-b-none"
}), $().ready(function() {
	$("#commentForm").validate();
	var e = "<i class='fa fa-times-circle'></i> ";
	$("#signupForm").validate({
		rules: {
			username: {
				required: !0,
				minlength: 2
			},
            tel: {
                required: true,
                minlength: 11,
                tel : true
            },
			password: {
				required: !0,
				minlength: 8,
                maxlength:16
			},
			confirm_password: {
				required: !0,
				equalTo: "#password"
			},
			email: {
				required: !0,
				email: !0
			},
			topic: {
				required: "#newsletter:checked",
				minlength: 2
			},
            code: {
                required: !0
            },
            textCode: {
                required: !0
            },
			agree: "required"
		},
		messages: {
			username: {
				required: e + "请输入您的用户名",
				minlength: e + "用户名不能为空"
			},
            tel: {
                required: e +"<p class='form-error'><i class='iconfont icon-baocuo'></i>请输入手机号码</p>",
                minlength: e + "<p class='form-error'><i class='iconfont icon-baocuo'></i>手机号为11位</p>",
                tel: e+ "<p class='form-error'><i class='iconfont icon-baocuo'></i>请输入正确的手机号码</p>"
            },
			password: {
				required: e + "<p class='form-error'><i class='iconfont icon-baocuo'></i>请输入您的密码</p>",
				minlength: e + "<p class='form-error'><i class='iconfont icon-baocuo'></i>密码必须在8个字符以上</p>",
				maxlength: e + "<p class='form-error'><i class='iconfont icon-baocuo'></i>密码不能超过16个字符</p>"
			},
			confirm_password: {
				required: e + "<p class='form-error'><i class='iconfont icon-baocuo'></i>请再次输入密码</p>",
				equalTo: e + "<p class='form-error'><i class='iconfont icon-baocuo'></i>两次输入的密码不一致</p>"
			},
			email: e + "请输入您的E-mail",
            code: e+ "<p class='form-error'><i class='iconfont icon-baocuo'></i>请输入验证码</p>",
            textCode: e+ "<p class='form-error'><i class='iconfont icon-baocuo'></i>请输入验证码</p>",
			agree: {
				required: e + "<p class='form-error'><i class='iconfont icon-baocuo'></i>您必须同意协议后才能注册</p>",
				element: "#agree-error"
			}
		}
	}), $("#username").focus(function() {
		var e = $("#firstname").val(),
			r = $("#lastname").val();
		e && r && !this.value && (this.value = e + "." + r)
	})
});
